mapper.select:
SELECT * FROM cb_company_cash_box
WHERE id = #{id}
AND status = 1

SELECT * FROM cb_company_cash_box
WHERE company_id = #{companyId}
AND status = 1

SELECT * FROM cb_company_cash_box
WHERE company_id = #{companyId}
ORDER BY `id` DESC limit 1

mapper.update:
UPDATE cb_company_cash_box
SET id = #{id}
 , `balance_provided_by_merchant` = #{balanceProvidedByMerchant} 
 , `balance_current` = #{balanceCurrent} 
 , `balance_total_provided_for_branch_cb` = #{balanceTotalProvidedForBranchCashBox} 
 , `balance_current_provided_for_branch_cb` = #{balanceCurrentProvidedForBranchCashBox} 
 , `balance_returned_from_branch_cb` = #{balanceReturnedFromBranchCashBox} 

WHERE id = #{id}

UPDATE cb_company_cash_box
SET id = #{id}
WHERE id = #{id}
